                                   selected_DataBank_Methods,                                 ...
                                   strategy_of_Formation_of_a_Single_Burst_Statistics_Matrix, ...
                                   vertical_Structure_of_all_Devices,                         ...
                                   selected_DataSet_Name,                                     ...
                                   resources_Folder_Address)

            % Level 1: Initial Paramater Extractions
                % Stage 1: Management of Input
                    vertical_Structure_of_all_Devices = Converter_to_Horizontal_or_Vertical_Vector ( vertical_Structure_of_all_Devices, 'Vertical', 'Multi Device Characteristic Extraction' );                       
                    
% % Stage 2: Seperation of Devices which should be used in formation of 'Data-Bank' based on user's preference                   
% 
% for index = 1 : size ( selected_Devices_for_Data_Making )
%     for device_Index = 1 : size ( vertical_Structure_of_all_Devices, 1 )
%         indices_of_Devices( device_Index, 1 ) = vertical_Structure_of_all_Devices ( device_Index, 1 ).device_Index;
% 
%     end
% end
    
            % Level 2: Collecting Fingerprints for Multi Devices 
                for device_Index = 1 : size ( vertical_Structure_of_all_Devices, 1 )
                    Vertical_Structure_of_a_Single_Device = vertical_Structure_of_all_Devices ( device_Index, 1 ).a_Single_Device;
                    [ matrix_of_Selected_Statistics_for_Current_Device, matrix_of_Selected_Fingerprints_for_Current_Device, cell_of_Names_of_Covered_Fields_for_current_Device, information_of_Bursts_for_Current_Device ] =  FingerPrints_for_a_Single_Device (    vertical_Cell_of_Characteristics_Names,                               ...                                          ... used here
                                                                                                                                                                                                                                                                    vertical_Cell_of_Statistics_Names,                                    ... used here
                                                                                                                                                                                                                                                                                                                                          ...
                                                                                                                                                                                                                                                                    strategy_of_Formation_of_a_Single_Burst_Statistics_Matrix,            ... 'all_Statistics_of_the_Same_Characteristics_in_Just_One_Row' or 'each_Statistics_of_Characteristics_in_Seperate_Rows'                                                                  
                                                                                                                                                                                                                                                                    selected_DataBank_Methods,                                            ...
                                                                                                                                                                                                                                                                    Vertical_Structure_of_a_Single_Device);

                    vertical_Structure_of_FingerPrint_DataBank_for_all_Devices ( device_Index, 1 ).fingerPrint_for_a_Single_Device           = matrix_of_Selected_Fingerprints_for_Current_Device;
                    vertical_Structure_of_FingerPrint_DataBank_for_all_Devices ( device_Index, 1 ).statistics_for_a_Single_Device            = matrix_of_Selected_Statistics_for_Current_Device;
                    vertical_Structure_of_FingerPrint_DataBank_for_all_Devices ( device_Index, 1 ).cell_of_FieldNames_for_a_Single_Device    = cell_of_Names_of_Covered_Fields_for_current_Device;
