                end                 

                address_of_Saving_DataBank       =      [ resources_Folder_Address   '\' selected_DataSet_Name '\DataBank\DataBank.mat' ];            

                save( address_of_Saving_DataBank, 'vertical_Structure_of_FingerPrint_DataBank_for_all_Devices')
                
                
    %% Section 3: Saving the 'strategy_of_Formation_of_a_Single_Burst_Statistics_Matrix'
                  % for the time of 'formation' of 'FingerPrintPlot' 
                        number_of_Devices = size ( vertical_Structure_of_FingerPrint_DataBank_for_all_Devices, 1 );
                        
                        lengths_Matrix_for_all_Devices = [];   
                        cell_of_FieldNames_for_all_Devices = [];
                        for  device_Index = 1 : number_of_Devices                            
                            number_of_Bursts_for_Current_Device = size ( vertical_Structure_of_FingerPrint_DataBank_for_all_Devices ( device_Index, 1 ).information_of_Bursts_for_Current_Device ,1 );                            
                            number_of_bursts_for_all_Devices ( device_Index, 1 ) = number_of_Bursts_for_Current_Device;
                            
                            cell_of_FieldNames_for_Current_Device = vertical_Structure_of_FingerPrint_DataBank_for_all_Devices ( device_Index, 1 ).cell_of_FieldNames_for_a_Single_Device;                            
                            cell_of_FieldNames_for_all_Devices    = [ cell_of_FieldNames_for_all_Devices, cell_of_FieldNames_for_Current_Device ];
                            
                            for burst_Index = 1 : number_of_Bursts_for_Current_Device
                                temp = vertical_Structure_of_FingerPrint_DataBank_for_all_Devices ( device_Index, 1 ).information_of_Bursts_for_Current_Device;
                                number_of_subRegions_for_Current_Burst = temp ( burst_Index, 1 ).number_of_subRegions;
                                
                                number_of_subRegions_in_a_Single_Burst = number_of_subRegions_for_Current_Burst;
                                temp_1 = temp ( burst_Index, 1 ).lengths_of_all_subRegions;
                                for subRegion_Index = 1 : number_of_subRegions_for_Current_Burst
                                    
                                    lengths_Matrix_for_a_Single_Device ( subRegion_Index, burst_Index  ) = temp_1 ( 1, subRegion_Index );
                                    lenght_of_a_Single_subRegion = lengths_Matrix_for_a_Single_Device (1,1);
                                    
                                end
                                
                            end
                            
                            lengths_Matrix_for_all_Devices = [ lengths_Matrix_for_all_Devices, lengths_Matrix_for_a_Single_Device ];
                        
                        end

                        temp_Address = [ resources_Folder_Address   '\' selected_DataSet_Name '\DataBank' ];                                                                                    
                        address_of_Saving_FPPlot_Strategy = [ temp_Address '\Single_Burst_Strategy_for_FPPlot.txt' ];                                                                                    
                        fid = fopen(address_of_Saving_FPPlot_Strategy,'w');
                        fprintf( fid, char( strategy_of_Formation_of_a_Single_Burst_Statistics_Matrix ) );
                        fclose(fid);
                        
                        save( [ temp_Address '\cell_of_FieldNames_for_all_Devices.mat' ] ,'cell_of_FieldNames_for_all_Devices' );
                        save( [ temp_Address '\lengths_Matrix_for_all_Devices.mat' ] ,'lengths_Matrix_for_all_Devices' );
                        save( [ temp_Address '\lenght_of_a_Single_subRegion.mat' ] ,'lenght_of_a_Single_subRegion' );
                        save( [ temp_Address '\number_of_subRegions_in_a_Single_Burst.mat' ] ,'number_of_subRegions_in_a_Single_Burst' );
                        save( [ temp_Address '\number_of_bursts_for_all_Devices.mat' ] ,'number_of_bursts_for_all_Devices' );
                        save( [ temp_Address '\number_of_Devices.mat' ] ,'number_of_Devices' );
                        save( [ temp_Address '\vertical_Cell_of_Statistics_Names.mat' ] ,'vertical_Cell_of_Statistics_Names' );
                        save( [ temp_Address '\vertical_Cell_of_Characteristics_Names.mat' ] ,'vertical_Cell_of_Characteristics_Names' );
                
                
